pragma solidity ^0.4.24;

import './SafeMath.sol'; 

// @notice This contract allows someone to leave ETH for another user in the case that they become unresponsive for x blocks
// TODO: can structure this to have the recipient claim creators death, which starts the proofExpiration. Problem with this approach is that we need a good way to notify the creator. 
contract Wills {
  using SafeMath for uint; 

  address public owner;

  mapping(bytes32 => Will) public will; 

  struct Will {
    address creator;
    address recipient; 
    uint amount;
    uint blocksBetweenProofs; 
    uint proofExpiration; 
    bool revokeable; 
  }


  // @notice constructor. Sets msg.sender as owner and will amount = amount ETH sent in this call.
  constructor()
  public {
    owner = msg.sender;
  }

  function createWill(address _recipient, uint _blocksBetweenProofs, bool _revokeable)
  external 
  payable 
  notZero(_blocksBetweenProofs)
  validAddress(_recipient)
  returns (bytes32 id) { 
    id = keccak256(abi.encodePacked(msg.sender, _recipient, msg.value));  // Note user cannot create another will with the same amount + recipient
    require(will[id].creator == address(0));   // Make sure struct isn't already created
    Will storage thisWill = will[id];
    thisWill.creator = msg.sender; 
    thisWill.recipient = _recipient; 
    thisWill.amount = msg.value;
    thisWill.blocksBetweenProofs = _blocksBetweenProofs; 
    thisWill.proofExpiration = _blocksBetweenProofs.add(block.number); 
    emit LogWillCreated(msg.sender, _recipient, msg.value, id); 
    return id; 
  }

  // TODO: Test that SafeMath throws in the case that creator tries to extend proofExpiration beyond one cycle (blocksBetweenProofs)
  function proveExistence(bytes32 _id)
  public
  onlyCaller(will[_id].creator) {
    Will storage thisWill = will[_id]; 
    require(thisWill.proofExpiration.sub(block.number) < thisWill.blocksBetweenProofs);  // Can only extend proofExpiration once
    thisWill.proofExpiration = thisWill.proofExpiration.add(thisWill.blocksBetweenProofs); 
  }


  function claimWill(bytes32 _id)
  public 
  onlyCaller(will[_id].recipient) {
    Will storage thisWill = will[_id]; 
    require(block.number > thisWill.proofExpiration);
    uint amountWEI = thisWill.amount;
    delete will[_id];
    msg.sender.transfer(amountWEI);
    emit LogWillClaimed(_id, msg.sender, amountWEI); 
  }


  function changeBlocksBetweenProofs(bytes32 _id, uint _newDesiredTime)
  external
  onlyCaller(will[_id].creator) 
  notZero(_newDesiredTime) {
      will[_id].blocksBetweenProofs = _newDesiredTime;
  }

  //////////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //                                            View functions 
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////////

  function getWill(bytes32 _id)
  public 
  view
  returns (address, address, uint, uint, uint, bool) { 
    return (will[_id].creator, will[_id].recipient, will[_id].amount, will[_id].blocksBetweenProofs, will[_id].proofExpiration, will[_id].revokeable); 
  }

  //////////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //                                            Modifiers
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////////

  // @notice reverts if msg.sender isn't the specified caller
  modifier onlyCaller(address _caller) {
    require(msg.sender == _caller);
    _;
  }

  // @notice reverts if will is not revokeable
  modifier isRevokeable(bytes32 _id) { 
    require(will[_id].revokeable); 
    _;
  }

  // @notice reverts if integer is 0
  modifier notZero(uint _number) { 
    require(_number > 0); 
    _;
  }

  modifier validAddress(address _addr) { 
    require(_addr != address(0)); 
    _;
  }

  //////////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //                                            Events
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////////

  event LogWillCreated(address indexed _creator, address _recipient, uint _amount, bytes32 _id); 
  event LogWillClaimed(bytes32 indexed _id, address _recipient, uint _amount); 

}
